syntax_editor_macro.text:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function Hello(strName, strGreeting)
	{
		strName = x_snc_devtools.GetStringValue(strName);
		strGreeting = x_snc_devtools.GetStringValue(strGreeting);
		if (strGreeting == '')
		{
			strGreeting = 'Hello';
		}
		if (strName != '')
		{
			return strGreeting + ' '+strName+'!';	
		}
		return strGreeting+'!';
	}


	function TestCase_Hello(strName, strGreeting, vExpected)
	{
		function TestCase_Hello_CheckResult(vInput,vExpected,vResult)
		{
			return (vExpected == vResult);
		}

		test.TestCase(
			'Function Hello()',
			Hello,
			{
				'Name': strName,
				'Greeting': strGreeting,
			},
			vExpected,
			TestCase_Hello_CheckResult);
	}

	TestCase_Hello(null,'Hi','Hi!');
	TestCase_Hello('','Good Morning','Good Morning!');
	TestCase_Hello('John','Salute','Salute John!');
	TestCase_Hello('Jane','Cheerio','Cheerio Jane!');
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
