function AppBuilder()
{
	x_snc_devtools.AppBuilder('x_your_scope',
		'x_parent_app_scope', // Optional: Only if this app "extends" another app's menu
		'Menu Separator', // Optional: Only if this app should inject a menu separator into another app
		20000 // Optional: The order of the submenu
	); 	
}
