sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_MakeUserName(vComponents, vExpected)
	{
		function TestCase_MakeUserName_CheckResult(vInput,vExpected,vResult)
		{
			return (vExpected == vResult);
		}

		test.TestCase(
			'Function MakeUserName()',
			MakeUserName,
			{
				'Components': vComponents
			},
			vExpected,
			TestCase_MakeUserName_CheckResult);
	}

	TestCase_MakeUserName(null,'');
	TestCase_MakeUserName('','');
	TestCase_MakeUserName('John','john');
	TestCase_MakeUserName(['Jane Doe'],'janedoe');
	TestCase_MakeUserName(['Jane','Doe'],'jane.doe');
	TestCase_MakeUserName(['Jane',' ','Doe'],'jane.doe');
	TestCase_MakeUserName([' Jane',' ','Doe '],'jane.doe');
	TestCase_MakeUserName(['    ',' Jane',' ','Doe '],'jane.doe');
	TestCase_MakeUserName(['Jane','Miller-Doe'],'jane.millerdoe');

	TestCase_MakeUserName(['TEST','SomePersona'],'test.somepersona');
	TestCase_MakeUserName(['TEST','SomeApp SomeRole'],'test.someapp.somerole');
	TestCase_MakeUserName(['test','SomeApp SomeRole'],'test.someapp.somerole');

	TestCase_MakeUserName(['TECHNICAL','SomeCapability'],'technical.somecapability');
	TestCase_MakeUserName(['technical','SomeCapability'],'technical.somecapability');
	TestCase_MakeUserName(['technical','SomeCapability SomethingSpecific'],'technical.somecapability.somethingspecific');
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
