sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_Clone(vInput, vExpected)
	{
		function TestCase_Clone_CheckResult(vInput,vExpected,vResult)
		{
			test.Log('GetType(vInput): '+GetType(vInput));
			test.Log('IsObject(vInput): '+IsObject(vInput));
			return IsEqual(vExpected, vResult);
		}

		test.TestCase(
			'Function Clone()',
			Clone,
			{
				'Input': vInput
			},
			vExpected,
			TestCase_Clone_CheckResult);
	}

	TestCase_Clone(null,null);
	TestCase_Clone('John','John');
	TestCase_Clone(42,42);
	TestCase_Clone(['Alpha','Beta'],['Alpha','Beta']);
	
	TestCase_Clone(
		{a:1,b:2,c:3},
		{a:1,b:2,c:3});
	
	var gr = new GlideRecord('kb_knowledge');
	gr.initialize();
	gr.short_description = 'Test';
	gr.text = 'Clone World';
	var strSysId = gr.insert();
	TestCase_Clone(Clone(gr),Clone(gr));		


	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);

