sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_StartWorker(strProgressName, strScriptInclude, strScriptIncludeMethod, params, vExpected)
	{
		function TestCase_StartWorker_CheckResult(vInput,vExpected,vResult)
		{
			if (IsValidSysId(vResult) == true)
			{
				var grProgressWorker = GetRecord('sys_progress_worker',vResult);
				test.Log('Progress Worker:\n'+RenderValue(grProgressWorker));
				var strState = GetStringValue(grProgressWorker.state);
				var bContinue = true;
				var nCount = 0;
				while (bContinue)
				{
					var grProgressWorker = GetRecord('sys_progress_worker',vResult);
					if (grProgressWorker.state != strState)
					{
						strState = GetStringValue(grProgressWorker.state);
						test.Log('Progress Worker:\n'+RenderValue(grProgressWorker));
					}
					if (strState == 'complete')
					{
						bContinue = false;
					}
					nCount++;
					if (nCount > 1000)
					{
						bContinue = false;
					}
				}
				
			}
			if (vExpected == true)
			{
				return IsValidSysId(vResult);
			}
			return (vResult == false);
		}

		
		test.TestCase(
			'Function StartWorker()',
			StartWorker,
			{
				'ProgressName': strProgressName,
				'Script Include': strScriptInclude,
				'Method': strScriptIncludeMethod,
				'Params': params
			},
			vExpected,
			TestCase_StartWorker_CheckResult);
	}


	TestCase_StartWorker('Test1','ScriptIncludeDoesNotExist','noMethod',{},false);
	
	TestCase_StartWorker('Test10','x_snc_devtools.DocumentationAPI','RenderCopyright',{ nCopyrightYear: '2023', strCopyrightHolder: 'Me' },true);

	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
