sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_RecordManageOrder(strTable,strColumn,strSysId,nNewOrder,vExpected)
	{
		function TestCase_RecordManageOrder_CheckResult(vInput,vExpected,vResult)
		{
			if (vExpected == false)
			{
				return (vResult === vExpected);
			}		
				
			var nOrder = 1;

			var grRecords = new GlideRecord(strTable);
			grRecords.orderBy(strColumn);
			grRecords.query();
			while (grRecords.next())
			{
				test.Log(grRecords.order+': '+grRecords.name);
			}
			GlideRecordRestoreLocation(grRecords);
			while (grRecords.next())
			{
				if (grRecords.order != nOrder)
				{
					test.Log('grRecords.order != nOrder');
					return false;
				}
				if (grRecords.sys_id == strSysId)
				{
					if (grRecords.order != nNewOrder)
					{
						if (nNewOrder > grRecords.getRowCount())
						{
							if (grRecords.order != grRecords.getRowCount())
							{
								test.Log('grRecords.order != grRecords.getRowCount()');
								return false;
							}
						}
						else if (nNewOrder < 1)
						{
							if (grRecords.order != 1)
							{
								test.Log('grRecords.order != 1');
								return false;
							}							
						}
						else
						{
							test.Log('grRecords.order != nNewOrder');
							return false;
						}
					}
				}
				nOrder++;
			}

			return true;
		}

		test.TestCase(
			'Function RecordManageOrder()',
			RecordManageOrder,
			{
				'Table': strTable,
				'Column': strColumn,
				'Sys Id': strSysId,
				'New Order': nNewOrder
			},
			vExpected,
			TestCase_RecordManageOrder_CheckResult);
	}

	TestCase_RecordManageOrder('bogus','nonsense','123',-3,false);
	TestCase_RecordManageOrder('incident','nonsense','123',-3,false);
	TestCase_RecordManageOrder('incident','order','123',-3,false);


	test.Log('Inserting Alpha without order value.');
	var grTest = new GlideRecord('x_snc_devtools_test');
	grTest.initialize();
	grTest.name = 'Alpha';
	var strSysIdAlpha = grTest.insert();

	var grRecords = new GlideRecord('x_snc_devtools_test');
	grRecords.orderBy('order');
	grRecords.query();
	while (grRecords.next())
	{
		test.Log(grRecords.order+': '+grRecords.name);
	}	
	
	test.Log('Alpha to 1');
	TestCase_RecordManageOrder('x_snc_devtools_test','order',strSysIdAlpha,1,true);
	test.Log('Alpha to -1');
	TestCase_RecordManageOrder('x_snc_devtools_test','order',strSysIdAlpha,-1,true);
	test.Log('Alpha to 999999');
	TestCase_RecordManageOrder('x_snc_devtools_test','order',strSysIdAlpha,999999,true);

	test.Log('Inserting Beta without order value');
	var grTest = new GlideRecord('x_snc_devtools_test');
	grTest.initialize();
	grTest.name = 'Beta';
	var strSysIdBeta = grTest.insert();	

	var grRecords = new GlideRecord('x_snc_devtools_test');
	grRecords.orderBy('order');
	grRecords.query();
	while (grRecords.next())
	{
		test.Log(grRecords.order+': '+grRecords.name);
	}		

	test.Log('Beta to 10');
	TestCase_RecordManageOrder('x_snc_devtools_test','order',strSysIdBeta,10,true);
	test.Log('Beta to 1');
	TestCase_RecordManageOrder('x_snc_devtools_test','order',strSysIdBeta,1,true);
	test.Log('Alpha to 1');
	TestCase_RecordManageOrder('x_snc_devtools_test','order',strSysIdAlpha,1,true);
	
	test.Log('Inserting Gamma with order value 1');
	var grTest = new GlideRecord('x_snc_devtools_test');
	grTest.initialize();
	grTest.name = 'Gamma';
	grTest.order = 1;
	var strSysIdGamma = grTest.insert();	

	var grRecords = new GlideRecord('x_snc_devtools_test');
	grRecords.orderBy('order');
	grRecords.query();
	while (grRecords.next())
	{
		test.Log(grRecords.order+': '+grRecords.name);
	}		

	test.Log('Alpha to 1');
	TestCase_RecordManageOrder('x_snc_devtools_test','order',strSysIdAlpha,1,true);
	test.Log('Gamma to 2');
	TestCase_RecordManageOrder('x_snc_devtools_test','order',strSysIdGamma,2,true);
	test.Log('Gamma to 3');
	TestCase_RecordManageOrder('x_snc_devtools_test','order',strSysIdGamma,3,true);
	test.Log('Alpha to 1000');
	TestCase_RecordManageOrder('x_snc_devtools_test','order',strSysIdAlpha,1000,true);
	test.Log('Alpha to 3');
	TestCase_RecordManageOrder('x_snc_devtools_test','order',strSysIdAlpha,3,true);
	
	test.Log('Maintain the sequence');
	TestCase_RecordManageOrder('x_snc_devtools_test','order',null,null,true);
	RecordSetValue('x_snc_devtools_test',strSysIdAlpha,'order',10);
	RecordSetValue('x_snc_devtools_test',strSysIdBeta,'order',20);
	RecordSetValue('x_snc_devtools_test',strSysIdGamma,'order',30);

	var grRecords = new GlideRecord('x_snc_devtools_test');
	grRecords.orderBy('order');
	grRecords.query();
	while (grRecords.next())
	{
		test.Log(grRecords.order+': '+grRecords.name);
	}	

	TestCase_RecordManageOrder('x_snc_devtools_test','order',null,null,true);


	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
