sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_HtmlRenderQRCode(strPayload, nWidth, strUnit, vExpected)
	{
		function TestCase_HtmlRenderQRCode_CheckResult(vInput,vExpected,vResult)
		{
			if (vExpected == false)
			{
				return (vResult == '');	
			}
			var nBlack = GetValue(vExpected,'black');
			if (nBlack > 0)
			{
				var aIndexes = StringFindMultiple(vResult,'#000000');
				var nBlackCount = aIndexes.length;
				if (nBlack != nBlackCount)
				{
					test.Log(StringFormat('Found {0} black tiles, but expecting {1}.',nBlackCount,nBlack));
					return false;
				}
			}
			var nWhite = GetValue(vExpected,'white');
			if (nWhite > 0)
			{
				var aIndexes = StringFindMultiple(vResult,'#FFFFFF');
				var nWhiteCount = aIndexes.length;
				if (nWhite != nWhiteCount)
				{
					test.Log(StringFormat('Found {0} white tiles, but expecting {1}.',nWhiteCount,nWhite));
					return false;
				}
			}			
			return true;
		}

		test.TestCase(
			'Function HtmlRenderQRCode()',
			HtmlRenderQRCode,
			{
				'Payload': strPayload,
				'Width': nWidth,
				'Unit': strUnit
			},
			vExpected,
			TestCase_HtmlRenderQRCode_CheckResult);
	}

	TestCase_HtmlRenderQRCode(null,null,null,false);
	
	TestCase_HtmlRenderQRCode('test',null,null,{ black: 226, white: 215});

	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
