sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_InstanceScanCheckOnApp(strCheckSysId, vApp, bDetailed, vExpected)
	{
		function TestCase_InstanceScanCheckOnApp_CheckResult(vInput,vExpected,vResult)
		{
			var bFinding = false;
			var aFindings = GetKeys(GetValue(vResult,'findings'));
			for (var nFinding = 0; nFinding < aFindings.length; nFinding++)
			{
				var strFinding = aFindings[nFinding];
				var finding = GetValue(vResult,'findings',strFinding);
				if (finding.check == strCheckSysId)
				{
					bFinding = true;
				}
			}
			return (vExpected == bFinding);
		}

		test.TestCase(
			'Function InstanceScanCheckOnApp()',
			InstanceScanCheckOnApp,
			{
				'Check': strCheckSysId,
				'App': vApp,
				'Detailed': bDetailed
			},
			vExpected,
			TestCase_InstanceScanCheckOnApp_CheckResult);
	}
	
	var grApp = GetAppRecord('x_snc_devtools');
	
	test.Log('Using a mock check against DevTools');
	var grCheck = new GlideRecord('scan_table_check');
	grCheck.initialize();
	grCheck.name = 'Test2';
	grCheck.short_description = 'Test2';
	grCheck.category = 'performance';
	grCheck.table = 'sys_script_include';
	grCheck.conditions = 'active=true^EQ';
	grCheck.priority = '1';
	grCheck.sys_scope = 'global';
	var strCheckSysId = GlideRecordInsert(grCheck);
	//var strScriptInclude = '773b28041b12e910cd6153d16b4bcbe2'; // Script Include x_snc_devtools.InstanceScanCheckOnApp	

	TestCase_InstanceScanCheckOnApp(null,null,false,false);
	TestCase_InstanceScanCheckOnApp(strCheckSysId,grApp,false,true);
	
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
