sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_SetAppRepo(vApp, strUrl, vExpected)
	{
		function TestCase_SetAppRepo_CheckResult(vInput,vExpected,vResult)
		{
			if (vExpected != vResult)
			{
				return false;
			}
			if (vExpected == true)
			{
				var grApp = new GlideRecord('sys_app');
				grApp.addQuery('sys_id','=',GetAppRecord(vInput['App']).sys_id);
				grApp.addQuery('x_snc_devtools_repo_url','=',vInput['Url']);
				grApp.query();
				if (grApp.getRowCount() != 1)
				{
					return false;
				}		
				
				var grRepo = new GlideRecord('sys_repo_config');
				grRepo.addQuery('sys_app','=',GetAppRecord(vInput['App']).sys_id);
				grRepo.addQuery('url','=',vInput['Url']);
				grRepo.query();
				if (grRepo.getRowCount() != 1)
				{
					return false;
				}
				
				var grDependency = new GlideRecord('x_snc_devtools_dependency');
				grDependency.addQuery('dependency','=',GetAppRecord(vInput['App']).sys_id);
				grDependency.addQuery('repo_url','!=',vInput['Url']);
				grDependency.query();
				if (grDependency.getRowCount() != 0)
				{
					return false;
				}					
			}
			return true;
		}

		test.TestCase(
			'Function SetAppRepo()',
			SetAppRepo,
			{
				'App': vApp,
				'Url': strUrl,
			},
			vExpected,
			TestCase_SetAppRepo_CheckResult);
	}

	TestCase_SetAppRepo(null,null,false);
	TestCase_SetAppRepo('x_snc_devtools',null,false);
	TestCase_SetAppRepo('x_snc_devtools','not_a_valid_url',false);
	TestCase_SetAppRepo('x_snc_devtools','https://github.com/example/example.git',true);
	TestCase_SetAppRepo('x_snc_devtools','https://github.com/saschawildgrube/servicenow-devtools.git',true);
		
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
