	test.SetResult(true);

	function TestCase_InstanceScanSuiteOnRecord(strSuiteSysId, strRecordTable, strRecordSysId, vExpected)
	{
		function TestCase_InstanceScanSuiteOnRecord_CheckResult(vInput,vExpected,vResult)
		{
			if (vExpected == false)
			{
				return (vResult == false);
			}
			return (vResult !== false);
		}	
		
		test.TestCase(
			'Function InstanceScanSuiteOnRecord()',
			InstanceScanSuiteOnRecord,
			{
				'SuiteSysId': strSuiteSysId,
				'RecordTable': strRecordTable,
				'RecordSysId':   strRecordSysId,
				'Detailed':   false
			},
			vExpected,
			TestCase_InstanceScanSuiteOnRecord_CheckResult);
	}

	var strSuiteSysId = '833655cc1b94101046e87733cd4bcb4e'; // Instance Security Center
	var grScriptInclude = GetRecord('sys_script_include','03c427d547261d10a4b0d4fe536d4351'); // InstanceScanSuiteOnRecord

	TestCase_InstanceScanSuiteOnRecord('','sys_script_include',grScriptInclude.sys_id,false);
	TestCase_InstanceScanSuiteOnRecord('','sys_script_include','12345678901234567890123456789012',false);
	TestCase_InstanceScanSuiteOnRecord(strSuiteSysId,'sys_script_include','12345678901234567890123456789012',false);

	TestCase_InstanceScanSuiteOnRecord(strSuiteSysId,'sys_script_include','03c427d547261d10a4b0d4fe536d4351',true);
	
