sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_GlideRecordAddQuery(grRecord, query, vExpected)
	{
		function TestCase_GlideRecordAddQuery_CheckResult(vInput,vExpected,vResult)
		{
			if (IsValidRecord(GetValue(vInput,'Record')) == false)
			{
				return true;
			}
			GlideRecordQuery(grRecord);
			
			return (vExpected == grRecord.getRowCount());
		}

		test.TestCase(
			'Function GlideRecordAddQuery()',
			GlideRecordAddQuery,
			{
				'Record': grRecord,
				'Query': query,
			},
			vExpected,
			TestCase_GlideRecordAddQuery_CheckResult);
	}

	

	TestCase_GlideRecordAddQuery(null,{},0);
	TestCase_GlideRecordAddQuery(new GlideRecord('x_snc_devtools_test'),{},0);
	
	var grTest = new GlideRecord('x_snc_devtools_test');
	grTest.initialize();
	grTest.name = 'Test';
	grTest.insert();
	TestCase_GlideRecordAddQuery(new GlideRecord('x_snc_devtools_test'),{},1);
	
	var grTest = new GlideRecord('x_snc_devtools_test');
	grTest.initialize();
	grTest.name = 'Test2';
	grTest.insert();
	TestCase_GlideRecordAddQuery(new GlideRecord('x_snc_devtools_test'),{},2);
	
	TestCase_GlideRecordAddQuery(new GlideRecord('x_snc_devtools_test'),{ name: 'Test2' },1);
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
