sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_StringReplaceRegEx(strHaystack, strPattern, strOptions, strReplace, vExpected)
	{
		test.TestCase(
			'Function StringReplaceRegEx()',
			StringReplaceRegEx,
			{
				'Haystack' : strHaystack,
				'Pattern'  : strPattern,
				'Options'  : strOptions,
				'Replace'  : strReplace
			},
			vExpected,
			null);
	}

	TestCase_StringReplaceRegEx(null, null, null, null, '');
	TestCase_StringReplaceRegEx('Hello World', '', '', '', 'Hello World');
	TestCase_StringReplaceRegEx('Hello World', 'World', '', 'Universe', 'Hello Universe');
	TestCase_StringReplaceRegEx('Hello1 World2', '[0-9]', '', '', 'Hello World');
	TestCase_StringReplaceRegEx('An escaped double quote: \\"', '\\\\"', '', '', 'An escaped double quote: ');
	
	
	TestCase_StringReplaceRegEx(
		'var result = sn_fd.Flow_API.getRunner().run();',
		'sn_fd\\s*\\.\\s*Flow_API\\s*\\.\\s*getRunner\\s*\\([a-zA-Z0-9\'"\\-_\\s\\(\\)\\.]+;',
		'',
		'placeholder;',
		'var result = placeholder;');
	
	// This case provokes a known deficiency: If the replacement string is part of the search pattern, the function will run into an infinit loop
	//TestCase_StringReplaceRegEx('I love cats', 'cats', '', 'dogs who love cats', 'I love dogs who love cats');
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
