sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_GetAppRecord(vInput, vExpected)
	{
		function TestCase_GetAppRecord_CheckResult(vInput,vExpected,vResult)
		{
			if (vExpected == false)
			{
				return (vExpected == vResult);
			}
			return IsValidRecord(vResult);
		}

		
		test.TestCase(
			'Function GetAppRecord()',
			GetAppRecord,
			{
				'Input': vInput 
			},
			vExpected,
			TestCase_GetAppRecord_CheckResult);
	}


	TestCase_GetAppRecord(null,false);
	TestCase_GetAppRecord('',false);
	TestCase_GetAppRecord('global',false);
	TestCase_GetAppRecord('Does not exist - hopefully',false);
	TestCase_GetAppRecord('glide',false);
	
	TestCase_GetAppRecord('d689f6901bd38450a89720a8ec4bcbfd',true);
	TestCase_GetAppRecord('x_snc_devtools',true);
	TestCase_GetAppRecord('DevTools',true);
	
	TestCase_GetAppRecord('sn_devstudio',true);
	TestCase_GetAppRecord('Studio',true);
	
	var grApp = new GlideRecord('sys_app');
	grApp.initialize();
	grApp.name = 'Alpha';
	grApp.scope = 'x_snc_alpha';
	GlideRecordInsert(grApp);
	
	var grApp = new GlideRecord('sys_app');
	grApp.initialize();
	grApp.name = 'Alpha Beta';
	grApp.scope = 'x_snc_alpha_be';
	GlideRecordInsert(grApp);

	TestCase_GetAppRecord('Alpha',true);
	TestCase_GetAppRecord('Alpha Beta',true);
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
