sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_ScriptGetFunctionNames(strScript, vExpected)
	{
		function TestCase_ScriptGetFunctionNames_CheckResult(vInput,vExpected,vResult)
		{
			return IsEqual(vExpected,vResult);
		}

		test.TestCase(
			'Function ScriptGetFunctionNames()',
			ScriptGetFunctionNames,
			{
				'Script': strScript 
			},
			vExpected,
			TestCase_ScriptGetFunctionNames_CheckResult);
	}



	TestCase_ScriptGetFunctionNames(null,false);
	TestCase_ScriptGetFunctionNames('',false);
	TestCase_ScriptGetFunctionNames('Hello World',false);
	
	TestCase_ScriptGetFunctionNames(
		"function Test() {}",
		['Test']);
	TestCase_ScriptGetFunctionNames(
		"function Test1() {}" +
		"function Test2() { function Test3() {} Test3(); }",
		['Test1','Test2','Test3']);
	TestCase_ScriptGetFunctionNames(
		"{ function Test1() {} }",
		['Test1']);	
	TestCase_ScriptGetFunctionNames(
		"(function Test1() { })();",
		[]);		
	TestCase_ScriptGetFunctionNames(
		"var obj = { Test1: function() {}, test2: 'Hello World' };",
		['Test1']);		
	TestCase_ScriptGetFunctionNames(
		"var Test1 = { }; Test1.Test2 = function() {};",
		['Test2']);		
	TestCase_ScriptGetFunctionNames(
		"function Test1() { var a = []; a.forEach(function(element) {} ) };",
		['Test1']);			
	
	TestCase_ScriptGetFunctionNames(
		"(function(a,y){})(); " +
		"function Test1(a,y){}; " +
		"function Test2(foo,bar) { var fiz = 'buz'; }; " +
		"var obj = { Test3: function(x,y) {}, a: 'b' }; " +
		"var omg = {};" +
		"omg.Test4 = function(x,y) {}; " +
		"function Test5(x) { function Test6(y) { } }; " +
		"(function(ano,nym){})(); " +
		"var arr = [ function (array1) {}  ];",
		['Test1','Test2','Test3','Test4','Test5','Test6']);	
	


	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
