sys_script_include.script:
function HtmlRenderRoundedBox(strInnerHtml)
{
	strInnerHtml = GetStringValue(strInnerHtml);
	if (strInnerHtml == '')
	{
		return '';
	}

	var strHtml = '';

	strHtml += '<div style="margin-left: 10px; margin-right: 10px; margin-bottom:5px; padding-top: 5px; padding-bottom: 2px; padding-left: 20px; padding-right:15px; border-radius: 10px; background-color: rgb(var(--now-color_surface--neutral-3))">';

	strHtml += strInnerHtml;
	strHtml += '</div>';

	return strHtml;
}
