sys_script_include.script:
function HtmlRenderPageGallery()
{
	var strHtml = '';

	strHtml += '<h1>DevTools Html Gallery</h1>';

	strHtml += HtmlRenderError('This is an error message'); 

	strHtml += HtmlRenderWarning('This is a warning message');	

	strHtml += HtmlRenderInfo('This is an information message');	

	var strCurrentTab = GetParam('sysparm_tab');
	if (strCurrentTab == '')
	{
		strCurrentTab = 'alpha';
	}
	strHtml += HtmlRenderTabNavigation(
		[
			{
				id: 'alpha',
				text: 'Alpha',
				url: '/x_snc_devtools_gallery.do?sysparm_tab=alpha'
			},
			{ 
				id: 'beta',
				text: 'Beta',
				url: '/x_snc_devtools_gallery.do?sysparm_tab=beta'
			},
			{ 
				id: 'gamma',
				text: 'Gamma',
				url: '/x_snc_devtools_gallery.do?sysparm_tab=gamma'
			}			
		],
		strCurrentTab);		

	strHtml += HtmlRenderTable(
		[
			[{ header: true, content: 'Header 1'},{ header: true, content: 'Header 2'}],
			['Row 1 Col 1','Row 1 Col 2'],
			['Row 2 Col 1','Row 2 Col 2']
		],
		'mytable',
		'table.mytable, th.mytable, td.mytable { border: 2px solid; padding: 2px; vertical-align: top }');	

	return strHtml;
}
