sys_ui_script.script:
function GetValue(vObject, vKey1, vKey2, vKey3, vKey4, vKey5, vKey6, vKey7, vKey8, vKey9, vKey10)
{
	if (vObject == undefined)
	{
		return null;
	}
	if (vKey1 == undefined)
	{
		return null;
	}
	if (IsArray(vKey1) == true)
	{
		if (vKey2 != null)
		{
			return null;
		}
		if (vKey1.length > 6)
		{
			return null;
		}
		return GetValue(
			vObject,
			GetValue(vKey1,0),
			GetValue(vKey1,1),
			GetValue(vKey1,2),
			GetValue(vKey1,3),
			GetValue(vKey1,4),
			GetValue(vKey1,5),
			GetValue(vKey1,6),
			GetValue(vKey1,7),
			GetValue(vKey1,8),
			GetValue(vKey1,9)
			);
	}
	if (vKey2 == undefined || vKey2 == null)
	{
		if (vObject[vKey1] == undefined)
		{
			return null;
		}
		var vValue = vObject[vKey1];
		return vValue;
	}
	return GetValue(vObject[vKey1],vKey2,vKey3,vKey4,vKey5,vKey6,vKey7,vKey8,vKey9,vKey10);
}
